import { promptUser } from './promptUtils.js';
import { lib } from '../../lang/en.js';
export function secretValuePrompt() {
    return promptUser([
        {
            name: 'secretValue',
            type: 'password',
            mask: '*',
            message: lib.prompts.secretPrompt.enterValue,
        },
    ]);
}
export function secretNamePrompt() {
    return promptUser([
        {
            name: 'secretName',
            type: 'input',
            message: lib.prompts.secretPrompt.enterName,
        },
    ]);
}
export function secretListPrompt(secrets, message) {
    return promptUser([
        {
            name: 'secretToModify',
            type: 'list',
            choices: secrets,
            message,
        },
    ]);
}
