import { logger as _logger } from '@hubspot/local-dev-lib/logger';
// Wrapper around LDL logger to provide type checking
export const uiLogger = {
    log: (message) => _logger.log(message),
    error: (message) => _logger.error(message),
    warn: (message) => _logger.warn(message),
    success: (message) => _logger.success(message),
    info: (message) => _logger.info(message),
    debug: _logger.debug,
    group: (message) => _logger.group(message),
    groupEnd: () => _logger.groupEnd(),
    json: (json) => console.log(JSON.stringify(json, null, 2)),
};
